mapper.delete:
DELETE FROM task
WHERE task_id = #{taskId}
AND task_list_id IN (
    SELECT task_list_id FROM task_list WHERE user_id = #{userId}
);

mapper.update:
UPDATE task
SET is_today = true,
updated_at   = CURRENT_TIMESTAMP,
period_day = #{periodDay}
WHERE task_id = #{taskId}
AND EXISTS (
SELECT 1
FROM task_list tl
WHERE tl.task_list_id = task.task_list_id
AND tl.user_id = #{userId}
)
